INSERT INTO `think_culture` VALUES ('1', '&lt;span style=&quot;color:#008000;&quot;&gt;&lt;span style=&quot;line-height:3em;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;经营理念：&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;line-height:3em;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;以人为本 &amp;nbsp;发展共赢 &amp;nbsp;&amp;nbsp;奉献社会&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#008000;&quot;&gt;&lt;span style=&quot;line-height:3em;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt; 核心竞争力：&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;line-height:3em;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;科技 &amp;nbsp;高效 &amp;nbsp;专业 &amp;nbsp;服务&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#008000;&quot;&gt;&lt;span style=&quot;line-height:3em;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt; 企业价值观：&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;line-height:3em;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;诚信 &amp;nbsp;务实 &amp;nbsp;创新 &amp;nbsp;超越&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#008000;&quot;&gt;&lt;span style=&quot;line-height:3em;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt; 企业精神：&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;line-height:3em;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;与时俱进 &amp;nbsp;&amp;nbsp;勇于超越&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#008000;&quot;&gt;&lt;span style=&quot;line-height:3em;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt; 企业愿景：&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;line-height:3em;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;成就自我 &amp;nbsp;成就未来&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#008000;&quot;&gt;&lt;span style=&quot;line-height:3em;&quot;&gt; &lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;基本方针：&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;line-height:3em;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;技术创新，严格管理；客户至上，质量为本；精心实施，服务第一&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#008000;&quot;&gt;&lt;span style=&quot;line-height:3em;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt; 员工守则：&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;line-height:3em;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;人人有职责，事事有程序，作业有标准，体系有监督，不良有纠正&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;');

-- ----------------------------
-- Table structure for think_email
-- ----------------------------
DROP TABLE IF EXISTS `think_email`;
CREATE TABLE `think_email` (
  `id` tinyint(3) unsigned NOT NULL AUTO_INCREMENT,
  `html` char(1) DEFAULT NULL COMMENT '是否允许发送“HTML代码”做为邮件的内容',
  `charset` varchar(255) DEFAULT NULL COMMENT '编码类型',
  `smtpauth` char(1) DEFAULT NULL COMMENT '是否需要身份验证',
  `smtpsecure` varchar(255) DEFAULT NULL COMMENT '加密方式',
  `from` varchar(255) DEFAULT NULL COMMENT '发送方邮件地址',
  `fromname` varchar(255) DEFAULT NULL COMMENT '发送方名称，会显示在邮件的内容中，可以自定义',
  `host` varchar(255) DEFAULT NULL COMMENT '发送邮件的服务协议地址，中转邮件服务器地址',
  `username` varchar(255) DEFAULT NULL COMMENT '发送方帐号',
  `password` varchar(255) DEFAULT NULL COMMENT '发送方帐号密码',
  `port` varchar(255) DEFAULT NULL COMMENT '发邮件端口号',
  `address` varchar(255) DEFAULT NULL COMMENT '收件人',
  `subject` varchar(255) DEFAULT NULL COMMENT '邮件标题',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

