-- ----------------------------
-- Records of think_news
-- ----------------------------
INSERT INTO `think_news` VALUES ('21', '华电恒瑞顺利通过国网山东省电力公司供应商资质能力核实', '&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;&lt;span style=&quot;line-height:3em;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;为拓展公司业务范围，规范公司生产行为，我公司参与&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-family:微软雅黑;line-height:3em;&quot;&gt;国网山&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;line-height:30px;background-color:#FFFFFF;&quot;&gt;东省电力公司2016年第二批10kV配网协议库存供应商资质能力核实活动，并顺利通过验收审查。&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;&lt;span style=&quot;line-height:3em;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;　　评估期间，国家电网山东省电力公司的专家考察了我公司生产现场，查验了产品生产过程、工作环境及产品各项试验数据、工艺控制记录，并对我公司的资质、业绩、管理体系、研发能力、产品鉴定情况等进行了现场核实。&lt;br /&gt;\r\n　　通过评估审查，&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:微软雅黑;line-height:3em;&quot;&gt;国网山&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:微软雅黑;line-height:30px;background-color:#FFFFFF;&quot;&gt;东省电力公司&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;&lt;span style=&quot;line-height:3em;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;专家对我公司的生产技术和生产能力给予肯定，同时，就现场管理、质量跟踪等方面提出了一些中肯的意见，希望我公司精益求精，为电网的经济安全运行做出新的更大的贡献！&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;', '1485413195', '1', '1', '29', '1', '/Uploads/News/1/2017-01-26/thumb_58899b4b5ba9d.jpg');
INSERT INTO `think_news` VALUES ('22', '我国首台AP1000核电机组将于明年底投运', '&lt;span style=&quot;line-height:1.5em;&quot;&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;&amp;nbsp;&amp;nbsp; 记者２９日从“２０１４年三代核电ＡＰ／ＣＡＰ合格供应商年会”获悉，原定２０１３年建成的我国首台也是全球首台第三代先进压水堆ＡＰ１０００型核电机组——浙江三门核电站１号机组，在克服设计固化和设备制造的种种困难后，将于２０１５年底并网发电。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;p&gt;\r\n	&lt;span style=&quot;line-height:1.5em;&quot;&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;　\r\n　国家核电技术公司董事长王炳华介绍，包括浙江三门两台和山东海阳两台在内的我国第三代核电自主化依托项目４台ＡＰ１０００机组目前建设总体进展顺利。首\r\n台即三门１号机组的核岛土建施工基本完成，正在进行系统移交与调试准备，明年底将实现并网发电。海阳１号机组晚其３个月，预计２０１６年初并网发电。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;line-height:1.5em;&quot;&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;　\r\n　ＡＰ１０００是我国从美国西屋公司引进的第三代核电技术。在引进消化吸收这项技术的基础上，我国正在开发具有自主知识产权的“升级版”ＣＡＰ１４００机\r\n型。王炳华介绍，目前ＣＡＰ１４００的初步设计已通过国家审查，施工设计完成约６５％。规划在山东荣成石岛湾建设ＣＡＰ１４００两台机组的各项前期工作进\r\n展顺利，预计今年内开工，２０１８年首台机组并网发电。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;line-height:1.5em;&quot;&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;　\r\n　据悉，４台ＡＰ１０００机组设备的平均国产化率达到５５％，ＣＡＰ１４００机组的国产化率超过８０％。王炳华说，该公司已认证通过了１０９家境内外合格\r\n核电设备供应商，涵盖机械、电气、材料、工程、仪控和运行服务等六大领域。我国已形成ＡＰ１０００、ＣＡＰ１４００机型每年６－８台的设备和材料供应能\r\n力，已形成一个面向全球、中外共享的ＡＰ／ＣＡＰ三代核电供应链。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;line-height:1.5em;&quot;&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;信息来源：新华网&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;', '1485412605', '1', '0', '3', '1', '/Uploads/News/1/2017-01-26/thumb_588998fd06243.jpg');
INSERT INTO `think_news` VALUES ('23', '国家能源局1日三文挺光伏要求电网年底加速并网', '&lt;span style=&quot;font-size:16px;&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 12月24日，国家能源局连发三项文件力挺光伏发展，要求电网力争使具备并网条件的项目在今年12月底前实现并网运行。同时，进一步推进分布式光伏发电示\r\n范区建设，数目从以前的18个增至30个。此外，太阳能发展“十三五”规划编制工作的时间表也浮出水面，要求2015年8月底形成全国规划送审稿。而按照\r\n此前发布的《能源发展行动战略计划》，2020年光伏装机目标将比“十二五”翻一番达到1亿千瓦左右。&lt;br /&gt;\r\n　　业内人士认为，这一举动是为冲刺完成年度目标。在年底抢装和并网政策利好下，今年完成新增并网10吉瓦没有问题。而分布式光伏作为国家力推的重点，景气攀升速度将更快。预计明年一季度光伏行业将出现“淡季不淡”的向好局面。&lt;br /&gt;\r\n　　数据显示，今年前三季度，全国光伏发电量约180亿千瓦时，相当于2013年全年发电量的200%。但前三季度，全国新增光伏发电并网容量仅为379万千瓦，距离相对保守的10吉瓦全年目标还有较大差距。&lt;br /&gt;\r\n　　“由于光伏电站与配套电网建设周期差别大、分布式光伏发电量大面广等原因，依然存在一定程度的光伏发电项目接网滞后问题。”24日发布的《国家能源局\r\n综合司关于做好2014年光伏发电项目接网工作的通知》提出，对今年12月底前能够完成主体工程且具备并网条件的光伏发电项目，电网企业要简化内部管理流\r\n程，提高工作效率，实现并网运行。&lt;br /&gt;\r\n　　多位业内人士认为，在这种情况下，国内完成10吉瓦甚至冲击12吉瓦的可能性还是很大。“一般大规模的地面电站，在年末将会涌现不约而同并网的现\r\n象。”光伏电站的并网热情，从组件供应情况可窥一斑。晶澳、天合、晶科和英利等一线组件厂商披露的相关材料显示，各家均已经满产，今年已没有空闲产能再接\r\n新订单。&lt;br /&gt;\r\n　　而分布式光伏也是国家政策重点扶持的对象。同一日下发的《国家能源局关于公布分布式光伏发电应用示范区建设的通知》明确，在国家能源局已公布的第一批\r\n18个分布式光伏发电应用示范区外，增加嘉兴光伏高新区等12个园区，如各省年度规模指标不足，可按照“先备案，后追加规模指标”方式建设和管理。&lt;br /&gt;\r\n　　“目前国家对分布式光伏电站项目的支持力度非常大，较高的投资回报收益也吸引了很多人进行投资。”英利光伏电力投资集团总经理张哲告诉《经济参考报》\r\n记者，分布式光伏电站拥有巨大的市场潜力，截至目前，公司在北京、广西、江苏、湖南等22个省市安装分布式电站项目699个，发展分布式经销商近200\r\n家。&lt;br /&gt;\r\n　　信息来源：《经济参考报》&lt;/span&gt;', '1485412655', '1', '0', '12', '1', '/Uploads/News/1/2017-01-26/thumb_5889992f7e778.jpg');
INSERT INTO `think_news` VALUES ('24', '我国将加快新能源微电网示范项目建设', '&lt;span style=&quot;color:#000000;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt; &lt;span style=&quot;font-size:14px;font-family:NSimSun;color:#000000;&quot;&gt;国家能源局22日对外发布《关于推进新能源微电网示范项目建设的指导意见》，提出加快推进新能源微电网示范工程建设，探索适应新能源发展的微电网技术及运营管理体制。&lt;/span&gt;\r\n&lt;p style=&quot;font-size:14px;color:#545454;font-family:Arial, 宋体, Helvetica, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-size:14px;font-family:NSimSun;color:#000000;&quot;&gt;　新能源微电网是基于局部配电网建设的，风、光、天然气等各类分布式能源多能互补的智慧型能源综合利用局域网，其具备较高新能源电力接入比例，可通过能量\r\n存储和优化配置实现本地能源生产与用能负荷基本平衡，可根据需要与公共电网灵活互动且相对独立运行。新能源微电网代表了未来能源发展趋势，可为新能源创造\r\n巨大发展空间。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;color:#545454;font-family:Arial, 宋体, Helvetica, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-size:14px;font-family:NSimSun;color:#000000;&quot;&gt;　　意见提出，新能源微电网示范项目建设要坚持四方面原则。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;color:#545454;font-family:Arial, 宋体, Helvetica, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-size:14px;font-family:NSimSun;color:#000000;&quot;&gt;　　一是因地制宜，创新机制。结合当地实际和新能源发展情况选择合理区域建设联网型微电网，在投资经营管理方面进行创新；在电网未覆盖的偏远地区、海岛等，优先选择新能源微电网方式，探索独立供电技术和经营管理新模式。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;color:#545454;font-family:Arial, 宋体, Helvetica, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-size:14px;font-family:NSimSun;color:#000000;&quot;&gt;　　二是多能互补，自成一体。将各类分布式能源、储电蓄热(冷)及高效用能技术相结合，通过智能电网及综合能量管理系统，形成以可再生能源为主的高效一体化分布式能源系统。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;color:#545454;font-family:Arial, 宋体, Helvetica, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-size:14px;font-family:NSimSun;color:#000000;&quot;&gt;　　三是技术先进、经济合理。集成分布式能源及智能一体化电力能源控制技术，形成先进高效的能源技术体系；与公共电网建立双向互动关系，灵活参与电力市场交易，使新能源微电网在一定的政策支持下具有经济合理性。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;color:#545454;font-family:Arial, 宋体, Helvetica, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;font-size:14px;font-family:NSimSun;color:#000000;&quot;&gt;　　四是典型示范、易于推广。首先抓好典型示范项目建设，因地制宜探索各类分布式能源和智能电网技术应用，创新管理体制和商业模式；整合各类政策，形成具有本地特点且易于复制的典型模式，在示范的基础上逐步推广。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;span&gt;&lt;/span&gt;', '1485413918', '1', '0', '6', '1', '/Uploads/News/1/2017-01-26/thumb_588999c29ab32.jpg');
INSERT INTO `think_news` VALUES ('25', 'KYN28A-12型开关设备', '&lt;br /&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;/Public/editor/php/../attached/image/20170126/20170126080501_27839.jpg&quot; alt=&quot;&quot; height=&quot;1284&quot; width=&quot;1000&quot; /&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;', '1485414550', '1', '0', '11', '3', '/Uploads/News/3/2017-01-26/thumb_58899fa6bc911.jpg');
