INSERT INTO `think_news` VALUES ('27', 'ZW7A-40.6型真空断路器', '&lt;img src=&quot;/Public/editor/attached/image/20170126/20170126081602_85698.jpg&quot; alt=&quot;&quot; height=&quot;1285&quot; width=&quot;1000&quot; /&gt;', '1485942083', '1', '1', '7', '3', '/Uploads/News/3/2017-01-26/thumb_5889a23ae794b.jpg');
INSERT INTO `think_news` VALUES ('28', '技术部岗位人员需求（技术支持工程师）', '&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;line-height:1.5em;&quot;&gt;&lt;span style=&quot;font-size:20px;&quot;&gt;&lt;strong&gt;技术支持工程师（若干名）&lt;/strong&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;strong&gt;工作职责&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;line-height:1.5em;&quot;&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;：&lt;br /&gt;\r\n1、熟悉行业动态和产品发展方向；&lt;br /&gt;\r\n2、负责售前技术洽谈、方案设计分析等工作；&lt;br /&gt;\r\n3、负责技术标书编制相关技术工作；&lt;br /&gt;\r\n4、负责产品报价相关技术支持性工作；&lt;br /&gt;\r\n5、负责合同实施方案设计、技术联络及相关文件编制工作；&lt;br /&gt;\r\n6、负责协助销售部门进行公司产品宣讲、推广等工作；&lt;br /&gt;\r\n7、负责项目文件资料收集整理归档等工作；&lt;br /&gt;\r\n8、完成领导交给的其他任务；&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;strong&gt;任职要求&lt;/strong&gt;：&lt;br /&gt;\r\n1、电气工程及其自动化相关专业本科及以上学历；&lt;br /&gt;\r\n2、具有三年以上规模化中高压开关类企业技术工作经验，熟悉电力系统相关知识及电力系统技术要求规范；&lt;br /&gt;\r\n3、熟悉产品的结构，并能够根据系统图，提出合理的设计方案；&lt;br /&gt;\r\n4、熟练使用OFFICE,Auto-CAD等相关专业软件；&lt;br /&gt;\r\n5、有责任心，团队协作能力强；&lt;/span&gt;&lt;/span&gt;', '1485421831', '1', '0', '14', '2', '/Uploads/News/2/2017-01-26/thumb_5889bd075a136.gif');
INSERT INTO `think_news` VALUES ('29', '技术部岗位人员需求（售后服务工程师）', '&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;span style=&quot;font-size:20px;&quot;&gt;&lt;strong&gt;售后服务工程师：&lt;/strong&gt;&lt;strong&gt;(&lt;/strong&gt;&lt;strong&gt;若干名&lt;/strong&gt;&lt;strong&gt;)&lt;/strong&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;span style=&quot;font-family:宋体;&quot;&gt;&lt;span style=&quot;line-height:1.5em;&quot;&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;strong&gt;工作职责&lt;/strong&gt;：&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-family:宋体;&quot;&gt;&lt;span style=&quot;line-height:1.5em;&quot;&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;1、负责现场售后服务工作的实施，如接货、安装、调试等工作；&lt;br /&gt;\r\n2、负责现场质量问题的处理、反馈、跟踪，并完成相应文件；&lt;br /&gt;\r\n3、负责给客户就有关产品使用、操作做咨询、培训、指导；&lt;br /&gt;\r\n4、负责现场需用物资的使用和管理；&lt;br /&gt;\r\n5、负责协助客户投诉的处理及客户满意度的调查；&lt;br /&gt;\r\n6、负责用户和服务相关电话的接听、记录、处理；&lt;br /&gt;\r\n7、收集反馈现场的产品信息，并对售后资料进行整理归档；&lt;br /&gt;\r\n8、完成领导交给的其他任务&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&lt;strong&gt;任职要求&lt;/strong&gt;：&lt;br /&gt;\r\n1、机械、电气、电力等相关类专业专科及以上学历；&lt;br /&gt;\r\n2、具备吃苦耐劳、善于沟通、动手能力强；&lt;br /&gt;\r\n3、具有1-2年以上本行业售后服务等相关经验。&lt;br /&gt;\r\n4、对待工作积极向上具有团队精神，适应经常性出差。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;', '1485421821', '1', '0', '6', '2', '/Uploads/News/2/2017-01-26/thumb_5889bcfd4196a.gif');
INSERT INTO `think_news` VALUES ('30', '商务部岗位人员需求(商务部助理）', '&lt;span style=&quot;font-family:宋体;&quot;&gt;&lt;span style=&quot;line-height:1.5em;&quot;&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;span style=&quot;font-size:20px;&quot;&gt;&lt;strong&gt;商务部助理&lt;/strong&gt;：&lt;strong&gt;(&lt;/strong&gt;&lt;strong&gt;两名&lt;/strong&gt;&lt;strong&gt;)&lt;/strong&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;strong&gt;工作职责:&lt;/strong&gt;&lt;br /&gt;\r\n1、承办具体合同谈判以及合同签订的后续跟踪事宜、&lt;br /&gt;\r\n2、协助部门经理完成其它日常工作。&lt;br /&gt;\r\n3、承办各项商务工作，代表公司与购销方联络&lt;br /&gt;\r\n4、维护与客户的良好关系以及建立持久联系，不断扩宽现有客户的产品渠道，丰富业务内容。&lt;br /&gt;\r\n5、一人跟单到底，把合同执行到最后。&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;strong&gt;任职要求：&lt;/strong&gt;&lt;br /&gt;\r\n1、本科以上学历，市场营销、经济管理等相关专业。&amp;nbsp;&lt;br /&gt;\r\n2、具有采购或相关行业经验者优先，英文水平优秀者优先 。&lt;br /&gt;\r\n3、具备一定的信息搜集能力，能吃苦耐劳。&lt;br /&gt;\r\n4、有良好的沟通表达能力，较强的商务谈判能力，态度端正，熟悉招投标流程。5、具有团队合作意识和良好的敬业精神。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;', '1485421801', '1', '0', '5', '2', '/Uploads/News/2/2017-01-26/thumb_5889bce96752a.gif');
INSERT INTO `think_news` VALUES ('31', '华春莹致名古屋市长河村隆之：请履行承诺跪地道歉', '&lt;p style=&quot;text-indent:2em;&quot;&gt;\r\n	【环球时报-环球网报道 记者 \r\n白云怡】当日本右翼酒店APA在客房摆置否认“南京大屠杀”书籍一事在中日两国间引发广泛议论之际，日本名古屋市市长河村隆之23日再次公然否认南京大屠\r\n杀。河村隆之称，如果日方真的屠杀了30万平民，那必须跪地道歉，但根本就不存在什么南京大屠杀。对此，中国&lt;span&gt;&lt;a href=&quot;http://t.qq.com/wj_xlt#pref=qqcom.keyword&quot; target=&quot;_blank&quot;&gt;外交部&lt;/a&gt;&lt;/span&gt;发言人华春莹24日在接受《环球时报》记者有关问询时表示，南京大屠杀这个事实在国际上早有公论，请这位市长去履行承诺。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;\r\n	在\r\n24日举行的例行记者会上，中国外交部发言人华春莹对《环球时报》记者表示，关于名古屋市长的有关言论，“我想告诉他的是，南京大屠杀是历史事实，国际上\r\n也早有公论。请这位市长去履行他的承诺。”华春莹称，中国愿与日方友好交流，但绝不会容忍歪曲历史、伤害中国人民感情的公然挑衅行为。不管什么人，如果恣\r\n意妄为，必然会付出代价。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;\r\n	据日本《朝日新闻》1月23日报道，在当天的名古屋市长例\r\n行记者会上，河村隆之对APA酒店和其董事长元谷外志雄的所作所为表示充分理解。他表示：“别管什么内容，那没啥大不了的”。河村称，“根本就没有平民被\r\n屠杀，只有一般的战争行为。政府见解也不认为发生过‘屠杀’嘛。”但《朝日新闻》之后引述了日本外务省网站上的相关表述：日本政府认为，日本军队1937\r\n年进入南京后，出现了杀害和抢劫非战斗人员的情况，这一点无法否认。\r\n&lt;/p&gt;\r\n针对日本极少数\r\n极右分子的挑衅，中国国家旅游局24日公布有关措施，要求所有中国出境旅游企业和旅游电商服务平台全面停止与APA酒店合作，停止使用该酒店作为地接酒店\r\n并下架所有该酒店的旅游产品及相关宣传广告，呼吁中国访日团组和广大游客，自觉抵制APA酒店的错误做法，不进该酒店消费。', '1485421640', '1', '0', '9', '1', '/Uploads/News/1/2017-01-26/thumb_5889bc4872362.jpg');
INSERT INTO `think_news` VALUES ('32', '特朗普再签行政命令 推翻奥巴马任内决定', '&lt;p style=&quot;text-indent:2em;&quot;&gt;\r\n	中新社华盛顿1月24日电 (记者 刁海洋)美国总统特朗普24日在白宫签署行政命令，决定重启美加“拱心石”XL输油管道项目。此举直接推翻了奥巴马任内否决该项目的决定。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;\r\n	特朗普当天上午在白宫椭圆办公室签署了上述行政命令。他表示，该项目可为美国创造2.8万个就业岗位。他说，该项目中的一些条款存在争议，需要重新进行协商。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;\r\n	“拱心石”XL输油管道项目是加拿大管道公司计划修建的一条全长1179英里(约1900公里)的输油管道，该管道从加拿大艾伯塔省跨境延伸至美国内布拉斯加州。奥巴马政府于2015年11月以不符合美国国家利益、削弱美国在气候变化问题上的国际领导力为由否决了该项目。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;\r\n	在白宫24日举行的记者会上，有记者向发言人斯派塞提问，美国究竟将重新协商哪些条款。斯派塞回应称，在以往的建设项目中，石油公司可赚取大把钞票，但普通工人却收益甚少。特朗普总统将最大程度为美国工人争取利益。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:2em;&quot;&gt;\r\n	同在24日，特朗普签署行政命令，批准北达科他州输油管道建设项目。在另外一份行政命令中，特朗普要求修建该项目所需的管道必须在美国国内生产。他说，“如果在美国修建管道，那么管道就应该在美国国内制造，这将给美国钢铁工人带来大量的工作机会。”\r\n&lt;/p&gt;\r\n特朗普在当天签署的另外两份行政命令中决定，更改输油管道项目以及基础设施建设项目的审批程序。他表示，需要加快推进环境评估和审批的程序，不能为了修一座桥或一条公路等上15年时间。', '1485421677', '1', '0', '11', '1', '/Uploads/News/1/2017-01-26/thumb_5889bc6d58ba3.jpg');

-- ----------------------------
-- Table structure for think_user
-- ----------------------------
DROP TABLE IF EXISTS `think_user`;
CREATE TABLE `think_user` (
  `id` tinyint(3) unsigned NOT NULL AUTO_INCREMENT COMMENT '用户id',
  `username` char(60) NOT NULL COMMENT '用户名',
  `password` char(32) NOT NULL COMMENT '用户密码',
  `logintime` char(10) NOT NULL COMMENT '上一次用户登陆时间',
  `nowlogintime` char(10) DEFAULT NULL COMMENT '当前的登录时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of think_user
-- ----------------------------
INSERT INTO `think_user` VALUES ('1', '123', '250cf8b51c773f3f8dc8b4be867a9a02', '1486454417', '1486454444');
